/* global document */
import m from 'manticore';
import ui from './packed-ui/alert';

// eslint-disable-next-line max-len
const b64Beep = 'data:audio/wav;base64,';
const pathToSuccessAudio = 'resources//sounds//success_card_read.mp3';

export default class ChromeAlert {
  static create(options, window, callback) {
    const w = window.contentWindow;
    let d = w.document.querySelector('#ppRetailAlert');
    if (!d) {
      const css = w.document.createElement('style');
      css.type = 'text/css';
      if (css.styleSheet) {
        css.styleSheet.cssText = ui.css;
      } else {
        css.appendChild(document.createTextNode(ui.css));
      }
      w.document.head.appendChild(css);

      d = w.document.createElement('dialog');
      d.id = 'ppRetailAlert';
      w.document.body.appendChild(d);
    }
    const a = new ChromeAlert(options, d, callback);
    a.show();
    return a;
  }

  constructor(options, dialog, callback) {
    this.callback = callback;
    this.options = options;
    this.dialog = dialog;

    dialog.innerHTML = this.assemble(options);
    const btns = dialog.querySelectorAll('button');
    const self = this;
    const clickFn = function clickFunc() {
      const ix = this.id.split('_')[1];
      callback(self, parseInt(ix, 10));
    };
    for (let b = 0; b < btns.length; b++) {
      btns[b].addEventListener('click', clickFn);
    }
  }

  addAudio(parts, audioInfo) {
    if (!audioInfo || !audioInfo.file) {
      return;
    }
    let src = '';
    if (audioInfo.file === 'success_card_read.mp3') {
      src = pathToSuccessAudio;
    } else if (audioInfo.file === 'beep') {
      src = b64Beep;
    }
    parts.push(`<audio id="audioTag" style="display:none;" src="${src}"/>`);
  }

  adImgInfo(parts, imgInfo) {
    if (!imgInfo) {
      return;
    }
    parts.push(`<div class="ppImgContainer"><img src="resources//images//${imgInfo}.png"/></div>`);
  }

  assemble(options) {
    const parts = [];
    parts.push(`<h1>${options.title || ''}</h1>`);
    parts.push(`<div class="ppMessage">${options.message || ''}</div>`);
    this.adImgInfo(parts, options.imageIcon);
    this.addAudio(parts, options.audio);
    if (options.showActivity) {
      parts.push(ui.html);
    }

    if (options.cancel) {
      options.buttons = options.buttons || [];
      options.buttons.push({ type: 'cancel', title: options.cancel });
    }

    this.buttons(parts, options.buttons);
    return parts.join('');
  }

  buttons(parts, buttons) {
    if (buttons && buttons.length) {
      for (let i = 0; i < buttons.length; i++) {
        let t = buttons[i];
        let cl;
        if (typeof t !== 'string') {
          cl = t.type === 'cancel' ? 'ppCancel' : null;
          t = t.title;
        }
        parts.push(`<button id="ppRetailAlert_${i}" ${cl ? (`class="${cl}"`) : ''}>${t}</button>`);
      }
    }
  }

  playAudio(count) {
    try {
      if (!count) {
        return;
      }

      const audioTag = this.dialog.querySelector('#audioTag');
      if (!audioTag) {
        return;
      }

      console.debug(`Playing audio. Remaining play counts : ${count}`); // eslint-disable-line no-console
      audioTag.play();
      m.setTimeout(() => {
        this.playAudio(count - 1);
      }, 450);
    } catch (e) {
      console.error(e); // eslint-disable-line no-console
    }
  }

  show() {
    if (!this.dialog.open) {
      this.dialog.showModal();
    }

    if (this.options.audio) {
      this.playAudio(this.options.audio.playCount || 1);
    }
  }

  /**
   * Does not call the callback
   */
  dismiss() {
    if (this.dialog) {
      if (this.dialog.open) {
        this.dialog.close();
      }
      delete this.dialog;
    }
    delete this.callback;
    delete this.options;
  }

  setTitle(title) {
    this.dialog.querySelector('h1').innerText = title;
  }

  setMessage(message) {
    this.dialog.querySelector('div.ppMessage').innerText = message;
  }
}
